function addclass(){let n=this.parentNode.parentNode;n.classList.add("focus")}function remclass(){let n=this.parentNode.parentNode;this.value==""&&n.classList.remove("focus")}const inputs=document.querySelectorAll(".input");inputs.forEach(n=>{n.addEventListener("focus",addclass),n.addEventListener("blur",remclass),n.value!=""&&n.parentNode.parentNode.classList.add("focus")});